import {Injectable} from '@angular/core';
import {HttpClient} from '@angular/common/http';

import {Observable} from 'rxjs';
import {ToastrService} from 'ngx-toastr';
import {catchError, map} from 'rxjs/operators';
import {Action, Selector, State, StateContext, Store} from '@ngxs/store';

import {environment} from '@app/env';
import {AdminActions} from './admin.action';

@State({
  name: 'admin',
  defaults: {
    funeralHomeSetting: {
      themeId: 0,
      mainColor: '#17313B',
      bannerURL: '',
      logoUrl: undefined
    },
    user: {
      email: undefined,
      emailVerified: undefined,
      firstName: undefined,
      lastName: undefined,
      username: undefined,
      roles: []
    },
    error: {
      code: undefined,
      message: undefined
    }
  }
})
@Injectable()
export class AdminState {

  @Selector()
  static errorCode(state): any {
    return state.error.code;
  }

  @Selector()
  static funeralHomeSetting(state): any {
    return state.funeralHomeSetting;
  }


  constructor(
    private http: HttpClient,
    private store: Store,
    private toastr: ToastrService
  ) {
  }

  @Action(AdminActions.SaveConfig)
  saveConfig(ctx: StateContext<any>, action: AdminActions.SaveConfig): void {
    const state = ctx.getState();
    ctx.setState({
      ...state,
      ...action
    });

    this.http.patch(`/funeralhomes/${action.config.userId}`, {
      themeId: action.config.themeId,
      mainColor: action.config.mainColor,
      bannerURL: action.config.bannerURL
    }).subscribe(res => {
      if (res) {
        this.toastr.success('Update success');
      }
    });
  }

  @Action(AdminActions.GetFuneralHomeSettings)
  getFuneralHomeSettings(
    ctx: StateContext<any>,
    action: AdminActions.GetFuneralHomeSettings
  ): Observable<any> {
    return this.http.get(`${environment.fakeUrl}/funeralhomes/${action.id}`)
      .pipe(
        map((setting: any) => {
          if (setting) {
            return ctx.patchState({
              funeralHomeSetting: {
                themeId: setting.themeId,
                mainColor: setting.mainColor,
                bannerURL: setting.bannerURL,
                logoUrl: setting.logoUrl
              }
            });
          }
        })
      );
  }

  @Action(AdminActions.GetConfig)
  getConfig(ctx: StateContext<any>): Observable<any> {
    const state = ctx.getState();

    return this.http.get(`${environment.fakeUrl}/settings`)
      .pipe(
        map(data => {
          ctx.setState({
            ...state,
            ...data
          });
        }),
        catchError((error: any) => {

          if (error) {
            return this.store.dispatch(
              new AdminActions.GetConfigFail(error.errorCode, error.message)
            );
          }

          return this.store.dispatch(
            new AdminActions.GetConfigFail('Unknow', 'Unknow')
          );
        })
      );
  }

  @Action(AdminActions.GetConfigFail)
  getConfigFail(ctx: StateContext<any>, action: AdminActions.GetConfigFail): void {
    ctx.patchState({
      error: {
        code: action.code,
        message: action.message
      }
    });
  }
}

import {Injectable} from '@angular/core';
import {ActivatedRouteSnapshot, RouterStateSnapshot, Router} from '@angular/router';

import {KeycloakAuthGuard, KeycloakService} from 'keycloak-angular';
import {Store} from "@ngxs/store";
import {from} from "rxjs";
import {AuthStorageService} from "@app/core/services";

@Injectable({
  providedIn: 'root'
})
export class AuthGuard extends KeycloakAuthGuard {
  constructor(
    protected readonly router: Router,
    protected readonly keycloak: KeycloakService,
    private authService: AuthStorageService
  ) {
    super(router, keycloak);
  }

  async isAccessAllowed(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot
  ): Promise<any> {


    // Force the user to log in if currently unauthenticated.
    if (!this.authenticated) {
      await this.keycloak.login({
        redirectUri: window.location.origin + state.url,
      });
    }

    // Get the roles required from the route.
    const requiredRoles = route.data.roles;

    // Allow the user to to proceed if no additional roles are required to access the route.
    if (!(requiredRoles instanceof Array) || requiredRoles.length === 0) {
      return true;
    }

    // Allow the user to proceed if all the required roles are present.
    return requiredRoles.every((role) => this.roles.includes(role));
  }
}

import {Injectable} from '@angular/core';
import {AuthStorageModel} from '@app/core/models/auth-storage-model';
import {KeycloakService} from 'keycloak-angular';
import {from, Observable, of} from 'rxjs';
import {KeycloakProfile} from 'keycloak-js';

@Injectable({
  providedIn: 'root'
})
export class AuthStorageService {

  authEnum = 'auth';
  isLoggedIn: boolean;

  constructor(
    private readonly keycloak: KeycloakService
  ) {
    this.getTokenFromKeyCloak();

    this.isAuthenticated()
      .pipe()
      .subscribe((isLoggedIn: boolean) => {
        this.isLoggedIn = isLoggedIn;
      });
  }

  private saveToStorage(authInfo: AuthStorageModel): void {
    window.localStorage[this.authEnum] = JSON.stringify(authInfo);
  }

  isAuthenticated(): Observable<boolean> {
    return from(this.keycloak.isLoggedIn());
  }

  isAuthenticatedPromise(): Promise<boolean> {
    return this.keycloak.isLoggedIn();
  }

  getLoggedInUserPromise(): Promise<KeycloakProfile> {
    return this.keycloak.loadUserProfile();
  }

  getLoggedInUser(): Observable<KeycloakProfile> {
    return from(this.keycloak.loadUserProfile());
  }

  getFromStorage(): AuthStorageModel {
    const authInfo = window.localStorage[this.authEnum];

    return authInfo ? JSON.parse(authInfo) as AuthStorageModel : undefined;
  }

  saveAuthInformation(authInfo: AuthStorageModel): void {
    this.saveToStorage(authInfo);
  }

  removeAuthInformation(): void {
    window.localStorage.removeItem('auth');
  }

  getToken(): string {
    const authInfo = this.getFromStorage();

    return authInfo ? authInfo.token : '';
  }

  updateToken(token: string): void {
    const authInfo = this.getFromStorage();
    this.saveToStorage({...authInfo, token});
  }

  getTokenFromKeyCloak(): Observable<any> {
    if (this.isLoggedIn) {

      this.keycloak.updateToken();

      const isTokenExpired = this.keycloak.isTokenExpired(0);


      if (!isTokenExpired) {
        from(this.keycloak.updateToken(50)).subscribe(updatedToken => {
          if (updatedToken) {
            return from(this.keycloak.getToken()).subscribe(newToken => {
              console.log('new token', newToken);
            });
          }
        });
      }

      return from(this.keycloak.getToken());
    }

    return of(null);
  }
}

import {Injectable} from '@angular/core';

@Injectable()
export class DateHelperService {
  getStringFromDate(date: Date): string {
    if (date) {
      let day = date.getUTCDate().toString();
      let month = (date.getMonth() + 1).toString();
      const year = date.getFullYear().toString();

      if (day.length < 2) {
        day = 0 + day;
      }

      if (month.length < 2) {
        month = 0 + month;
      }

      return `${day}/${month}/${year}`;
    }
  }

  getDateRangeFromString(dateString: string): Array<Date> {

    if (!dateString) {
      return null;
    }

    const rangeDateArray = dateString
      .replace(/\s/g, '')
      .split('-');

    const fromArray = rangeDateArray[0];
    const toArray = rangeDateArray[1];

    const fromDate = fromArray.split('/')[0];
    const fromMonth = fromArray.split('/')[1];
    const fromYear = fromArray.split('/')[2];

    const toDate = toArray.split('/')[0];
    const toMonth = toArray.split('/')[1];
    const toYear = toArray.split('/')[2];

    const checkValidDateFrom = this.isValidDate(fromDate, fromMonth, fromYear);
    const checkValidDateTo = this.isValidDate(toDate, toMonth, toYear);

    if (!checkValidDateFrom || !checkValidDateTo) {
      return null;
    }

    const from = new Date(
      parseInt(fromYear, 0),
      parseInt(fromMonth, 0) - 1,
      parseInt(fromDate, 0)
    );

    const to = new Date(
      parseInt(toYear, 0),
      parseInt(toMonth, 0) - 1,
      parseInt(toDate, 0)
    );

    const today = new Date();

    if (+from > +to || +to > +today) {
      return null;
    }

    return [from, to];
  }

  newDate(dayString: string, monthString: string, yearString: string): Date {
    return new Date(
      parseInt(yearString, 0),
      parseInt(monthString, 0) - 1,
      parseInt(dayString, 0));
  }


  isValidDate(dateInput: string, monthInput: string, yearInput: string): boolean {
    const day = parseInt(dateInput, 10);
    const month = parseInt(monthInput, 10);
    const year = parseInt(yearInput, 10);

    // Check the ranges of month and year
    if (year < 1000 || year > 3000 || month === 0 || month > 12) {
      return false;
    }

    const monthLength = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

    // Adjust for leap years
    if (year % 400 === 0 || (year % 100 !== 0 && year % 4 === 0)) {
      monthLength[1] = 29;
    }

    // Check the range of the day
    return day > 0 && day <= monthLength[month - 1];
  }
}

import { OnDestroy, Injectable } from '@angular/core';
import { Subject } from 'rxjs';

@Injectable()
export class DestroyableComponent implements OnDestroy {

  protected componentDestroyed$: Subject<any>;

  constructor() {
    this.componentDestroyed$ = new Subject();
  }

  ngOnDestroy(): void {
    this.componentDestroyed$.next(null);
    this.componentDestroyed$.complete();
  }

}

import {Injectable} from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class JwtService {
  constructor() {
  }

  parseJwt(token: any): string {
    const base64Url = token.split('.')[1];
    const base64 = decodeURIComponent(atob(base64Url).split('').map((c: any) => {
      return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(base64);
  }
}

import { Injectable } from '@angular/core';

@Injectable()
export class LocalStorageService {
  private storage: Storage;

  constructor() {
    if (!window.localStorage) {
      throw new Error('Current browser does not support Local Storage');
    }
    this.storage = localStorage;
  }

  exists(key: string): boolean {
    return !!this.storage.getItem(`${key}`);
  }

  getItem(key: string): any {
    const result = this.storage.getItem(`${key}`);
    if (result === undefined || result == null) {
      return null;
    }

    try {
      return JSON.parse(result);
    } catch (e) {
      return result;
    }
  }

  setItem(key: string, value: any) {
    this.storage.setItem(`${key}`, JSON.stringify(value));
  }

  removeItem(key: string) {
    return this.storage.removeItem(`${key}`);
  }

  clear() {
    return this.storage.clear();
  }

  saveToUser(user: any, data: any) {
    const localData = this.getItem(user?.id.toString());
    this.setItem(user?.id.toString(), {
      ...localData,
      ...data
    });
  }

  getFromUser(user: any, key?: string) {
    const localData = this.getItem(user?.id.toString());
    if (key) {
      return key && localData && localData[key];
    }

    return localData;
  }
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { Router } from '@angular/router';

import { DatePipe } from '@angular/common';
import { catchError, map, tap } from 'rxjs/operators';
import { from, Observable, of, Subscription, timer } from 'rxjs';
import { Action, State, StateContext, Store } from '@ngxs/store';

import { User } from '../models/user.model';
import { MemorialActions } from './memorial.actions';
import { environment } from "@app/env";

@State({
  name: 'memorial',
  defaults: {
    users: undefined,
    citiesRegions: undefined,
    funeralHomeSetting: {
      id: undefined,
      adminName: undefined,
      name: undefined,
      themeId: undefined,
      mainColor: undefined,
      bannerURL: undefined,
      photoUrl: undefined
    },
    isLoading: false,
    isLoadingRegion: false,
    error: {
      code: undefined,
      message: undefined
    }
  }
})
@Injectable()
export class MemorialState {

  subscription: Subscription;
  searchPortalUrl = '/portal/search';

  constructor(
    private http: HttpClient,
    private store: Store,
    private router: Router
  ) {
  }

  refreshSubscription(userId: number): any {
    this.subscription = timer(5000).subscribe(() => {
      this.store.dispatch(new MemorialActions.GetFuneralHomeSettings(userId))
        .pipe()
        .subscribe(() => {
          if (!this.router.url.includes('memorial')) {
            return this.subscription.unsubscribe();
          }
        });
    });
  }

  @Action(MemorialActions.GetUsers)
  getUsers(ctx: StateContext<any>, action: MemorialActions.GetUsers): Observable<any> {
    let params = new HttpParams();
    params = params.append('size', action.size.toString());
    params = params.append('domain', 'vareminnesider.no');
    params = params.append('orderBy', 'memorial_page,desc');

    return from(import(`src/assets/data.json`))
    // return this.http.get(this.searchPortalUrl, { params })
      .pipe(
        tap(ctx.patchState({ isLoading: true })),
        tap(() => {

        }),
        map((response: any) => {
          if (response) {
            const userMap = this.mapUserData(response.content);
            ctx.patchState({ isLoading: false, users: userMap });
          }
        }),
        catchError((error) => {
          if (error) {
            return this.store.dispatch(new MemorialActions.GetUsersFailed(
              error.errorCode,
              error.message
            ));
          }

          return this.store.dispatch(new MemorialActions.GetUsersFailed(
            'Unknow', 'Unknow'
          ));
        })
      );
  }

  @Action(MemorialActions.GetUsersFailed)
  getUsersFailed(ctx: StateContext<any>, action: MemorialActions.GetUsersFailed): void {
    ctx.patchState({
      users: [],
      isLoading: false,
      error: { code: action.errorCode, message: action.message }
    });
  }

  @Action(MemorialActions.SearchLocally)
  searchLocally(ctx: StateContext<any>, action: MemorialActions.SearchLocally): Observable<any> {
    let params = new HttpParams();
    params = params.append('orderBy', 'memorial_page,desc');

    Object.entries(action.searchParams).forEach(([key, value]) => {
      if (value instanceof Date) {
        value = new DatePipe('en-US').transform(value, 'yyyy-MM-dd');
      }

      if (value) {
        params = params.append(key, value);
      }
    });

    return this.makeSearchPortalRequest(ctx, params);
  }

  @Action(MemorialActions.GetCityAndRegion)
  getCityAndRegion(ctx: StateContext<any>): Observable<any> {
    const url = '/portal/regions';
    let params = new HttpParams();
    params = params.append('domain', this.getDomain());

    return this.http.get(url, { params })
      .pipe(
        tap(ctx.patchState({ isLoadingRegion: true })),
        map((response: any) => {
          if (response) {
            ctx.patchState({
              isLoadingRegion: false,
              citiesRegions: [
                { id: 0, name: 'All countries', cities: [] },
                ...response
              ]
            });
          }
        }),
        catchError(error => {
          ctx.patchState({ isLoadingRegion: false });
          return of(error);
        })
      );
  }


  @Action(MemorialActions.GetFuneralHomeSettings)
  getFuneralHomeSettings(ctx: StateContext<any>, action: MemorialActions.GetFuneralHomeSettings): Observable<any> {

    return this.http.get(`${environment.fakeUrl}/funeralhomes/${action.id}`).pipe(
      tap(() => this.refreshSubscription(action.id)),
      map((response) => {
        if (response) {
          ctx.patchState({
            funeralHomeSetting: response,
            isLoading: false
          });
        }
      }),
    );
  }

  @Action(MemorialActions.SortMemorialPage)
  sortMemorialPage(ctx: StateContext<any>, action: MemorialActions.SortMemorialPage): Observable<any> {
    let params = new HttpParams();
    params = params.append('orderBy', `${action.sortValue.param},${action.sortValue.value}`);

    return this.makeSearchPortalRequest(ctx, params);
  }

  @Action(MemorialActions.FilterMemorialPage)
  filterMemorialPage(ctx: StateContext<any>, action: MemorialActions.FilterMemorialPage): Observable<any> {
    let params = new HttpParams();
    params = params.append('filterBy', action.filterValue.param);

    return this.makeSearchPortalRequest(ctx, params);
  }

  @Action(MemorialActions.SearchBox)
  searchBox(ctx: StateContext<any>, action: MemorialActions.SearchBox): Observable<any> {
    const encodedSearchTerm = encodeURI(action.searchString);
    let params = new HttpParams().append('searchTerm', encodedSearchTerm);

    return this.makeSearchPortalRequest(ctx, params);
  }

  makeSearchPortalRequest(ctx: StateContext<any>, params: HttpParams): Observable<any> {
    // params = params.append('domain', 'vareminnesider.no');
    params = params.append('domain', this.getDomain());

    return this.http.get(this.searchPortalUrl, { params })
      .pipe(
        tap(ctx.patchState({
          isLoading: true,
          error: { code: null, message: null }
        })),
        map((response: any) => {
          if (response) {
            const userMap = this.mapUserData(response.content);
            ctx.patchState({ users: userMap, isLoading: false });
          }
        }),
        catchError(error => {
          this.store.dispatch(new MemorialActions.GetUsersFailed(error.errorCode, error.message));
          return of(error);
        })
      );
  }

  mapUserData(data: any): Array<User> {
    return data.map(user => {
      return {
        birthdate: user.birthdate,
        deathdate: user.deathdate,
        firstName: user.firstName,
        lastName: user.lastName,
        photoUrl: user.photoUrl ? user.photoUrl : '',
        deathCity: user.deathCity,
        memorialPageCreated: user.memorialPageCreated,
        flowerShopActivated: user.flowerShopActivated,
        donationActivated: user.donationActivated,
        publishOnWebDate: user.publishOnWebDate,
        memorialPage: {
          visible: user.memorialPage.visible,
          url: user.memorialPage.url
        },
        donation: {
          visible: user.donation.visible,
          url: user.donation.url,
        },
        flowerShop: {
          visible: user.flowerShop.visible,
          url: user.flowerShop.url,
        },
        deathNotice: {
          visible: user.deathNotice.visible,
          url: user.deathNotice.url,
        },
      };
    });
  }

  getDomain(): string {
    return location.origin.replace(/(^\w+:|^)\/\//, '');
  }
}

import {Injectable} from '@angular/core';

const APP_PREFIX = 'APP__';

@Injectable()
export class SessionStorageService {
  private storage: Storage;

  constructor() {
    if (!window.sessionStorage) {
      throw new Error('Current browser does not support sessionStorage');
    }
    this.storage = localStorage;
  }

  exists(key: string): boolean {
    return !!this.storage.getItem(`${APP_PREFIX}${key}`);
  }

  getItem(key: string): string {
    const result = this.storage.getItem(`${APP_PREFIX}${key}`);

    if (result === undefined || result == null) {
      return null;
    }

    try {
      return JSON.parse(result);
    } catch (e) {
      return result;
    }
  }

  setItem(key: string, value: any): void {
    this.storage.setItem(`${APP_PREFIX}${key}`, JSON.stringify(value));
  }

  removeItem(key: string): void {
    return this.storage.removeItem(`${APP_PREFIX}${key}`);
  }

  clear(): void {
    return this.storage.clear();
  }
}

import {Injectable} from '@angular/core';

import {from, Observable} from 'rxjs';
import {TRANSLOCO_LOADER, Translation, TranslocoLoader} from '@ngneat/transloco';

@Injectable()
export class TranslocoHttpLoader implements TranslocoLoader {

  constructor() {}

  getTranslation(langPath: string): Observable<Translation> {
    return from(import(`src/assets/i18n/${langPath}.json`));
  }
}

export const translocoLoader = {
  provide: TRANSLOCO_LOADER,
  useClass: TranslocoHttpLoader
};

