import {Injectable} from '@angular/core';
import {HttpEvent, HttpInterceptor, HttpHandler, HttpRequest} from '@angular/common/http';
import {Observable} from 'rxjs';

import {environment} from '@app/env';

@Injectable()
export class ApiPrefixInterceptor implements HttpInterceptor {

  intercept(request: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {

    const hasBaseURL = /^(http|https):/i.test(request.url);

    if (!hasBaseURL
      && request.url.indexOf('assets/locale') < 0
      && request.url.indexOf('assets/images') < 0
    ) {
      const url = environment.production
        ? `${environment.apiBaseURL}${request.url}`
        : `${environment.apiBaseURL}${request.url}`;

      request = request.clone({url});
    }

    return next.handle(request);
  }
}

import {Injectable} from '@angular/core';
import {HttpEvent, HttpInterceptor, HttpHandler, HttpRequest, HttpErrorResponse} from '@angular/common/http';

import {Observable, throwError} from 'rxjs';
import {catchError} from 'rxjs/operators';

import {HttpError} from '../models/http-error';

@Injectable()
export class ErrorHandlerInterceptor implements HttpInterceptor {

  constructor() {}

  intercept(request: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    return next.handle(request).pipe(
      catchError((error) => {
        return this.errorHandler(error);
      })
    );
  }

  private errorHandler(error: HttpErrorResponse): Observable<never> {
    let err = error.error;
    if (typeof error.error === 'string') {
      err = JSON.parse(error.error);
    }

    const responseError: HttpError = {
      message: error.message,
      status: error.status,
      statusText: error.statusText,
      errorCode: err.errorCode
    };

    if (error.status === 0) {
      responseError.errorCode = 'Unknown Error';
    }

    return throwError(responseError);
  }

}

import { Injectable } from '@angular/core';
import { HttpInterceptor, HttpHandler, HttpEvent, HttpRequest } from '@angular/common/http';

import { Observable } from 'rxjs';
import { LocalStorageService } from '../web-storage';

@Injectable({
  providedIn: 'root',
})
export class HttpDeviceInterceptor implements HttpInterceptor {
  constructor(private localStorageService: LocalStorageService) { }

  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    let tokenReq: any;
    const deviceId = this.localStorageService.getItem('deviceId');

    if (deviceId) {
      tokenReq = req.clone({
        headers: req.headers.set('Device-id', deviceId)
      });
    } else {
      return next.handle(req);
    }

    return next.handle(tokenReq);
  }
}

import {Injectable} from '@angular/core';
import {HttpInterceptor, HttpHandler, HttpEvent, HttpRequest} from '@angular/common/http';

import {Observable} from 'rxjs';
import {switchMap} from 'rxjs/operators';

import {AuthStorageService} from '../services';

@Injectable({
  providedIn: 'root',
})
export class HttpTokenInterceptor implements HttpInterceptor {

  constructor(private authStorageService: AuthStorageService) {
  }

  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {

    let tokenRequest: any;

    return this.authStorageService.getTokenFromKeyCloak()
      .pipe(
        switchMap((token: any) => {
          if (token) {
            tokenRequest = req.clone({
              headers: req.headers.set('Authorization-token', token)
            });
          } else {
            return next.handle(req);
          }

          return next.handle(tokenRequest);
        }),
      );
  }
}

import {Injectable} from '@angular/core';
import {HttpInterceptor, HttpRequest, HttpHandler, HttpEvent} from '@angular/common/http';

import {Observable} from 'rxjs';
import {map} from 'rxjs/operators';

@Injectable()
export class SuccessHandlerInterceptor implements HttpInterceptor {

  constructor() {}

  intercept(request: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    return next.handle(request).pipe(
      map((response: any) => {
        if (
          request.method === 'GET' &&
          response.type === 4 &&
          response.status === 200
        ) {
          const body = response.body;
          if (body.content && body.pageable) {

            return response.clone({
              body: {
                content: body.content,
                pageable: {
                  totalPages: body.totalPages,
                  totalItems: body.totalElements,
                  pageSize: body.pageable.pageSize,
                  pageNumber: body.pageable.pageNumber
                }
              }
            });
          }
        }

        return response;
      })
    );
  }
}

import {Injectable} from '@angular/core';
import {HttpInterceptor, HttpHandler, HttpEvent, HttpRequest} from '@angular/common/http';
import {Router} from '@angular/router';

import {Store} from '@ngxs/store';
import {Observable} from 'rxjs';
import {tap} from 'rxjs/operators';

@Injectable({
  providedIn: 'root',
})
export class UnauthorizedInterceptor implements HttpInterceptor {

  constructor(
    private router: Router,
    private store: Store
  ) {
  }

  intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
    return next.handle(req).pipe(
      tap(() => {
        },
        (err) => {
          if (err.status === 401) {
            if (!this.router.url.includes('signin')) {
              return null;
              // return this.store.dispatch(new Logout());
            }
          }
        }),
    );
  }
}

