import { Component, Input, ViewChild, ViewEncapsulation } from '@angular/core';
import { NgSelectComponent } from '@ng-select/ng-select';
import { from } from 'rxjs';
import { map, tap } from 'rxjs/operators';

@Component({
  selector: 'adstate-advanced-filter',
  templateUrl: './filter.html',
  styleUrls: ['./filter.scss'],
  encapsulation: ViewEncapsulation.None
})
export default class AdvancedFilterComponent {

  @Input() label: string = 'Filter';

  selectedItems = [];
  isSelectAllItem: boolean = false;
  isCollapsed = true;
  regions = [];
  cities = [];
  currentLanguage = 'en';
  datepickerPlacement = 'bottom';
  selectedCity: any;

  citiesAndRegions$ = from(import('../assets/cities-regions.json')).pipe(
    tap((citiesAndRegions) => {
      this.regions = citiesAndRegions.data
    }),
    map(citiesAndRegions => citiesAndRegions.data),
  );

  items = [
    { label: 'Memorial page', isSelected: false },
    { label: 'Have donations', isSelected: false },
    { label: 'Has death notice', isSelected: false },
    { label: 'Has flower shop', isSelected: false  }
  ];

  datePickerConfig = {
    containerClass: 'theme-adstate',
    showClearButton: true
  };
  
  @ViewChild('citySelect') ngSelectComponent: NgSelectComponent;

  constructor() {
  }

  onSelectAllItem() {
    this.isSelectAllItem = !this.isSelectAllItem;

    console.log('this.isSelectAllItem', this.isSelectAllItem);

    this.items = this.items.map(item => {
      return {
        ...item,
        isSelected: this.isSelectAllItem
      }
    });

    if (this.isSelectAllItem) {
      return this.selectedItems = this.items;
    }

    return this.selectedItems = [];
  }

  onItemSelect(index) {
    this.items[index].isSelected = !this.items[index].isSelected;
    this.selectedItems = this.items.filter(item => item.isSelected);

    if (this.selectedItems.length === 4) {
      this.isSelectAllItem = true;
    }

    if (this.selectedItems.length === 0) {
      this.isSelectAllItem = false;
    }
  }

  toggleAdvancedSearch(): void {
    this.isCollapsed = !this.isCollapsed;
  }

  onSelectRegion(region): void {

    const citiesByRegion = this.regions.find(reg => reg.id === region.id);
    this.ngSelectComponent.handleClearClick();

    this.cities = [
      { id: 0, name: 'All cities' },
      ...citiesByRegion.cities
    ];

  }

  compareSelectedValue(item, selected): boolean {
    return item.id === selected.id;
  }

  onSelectCity(event): void { }

}

