@Component({
  selector: 'adstate-banner',
  template: `
    <div *ngIf="imageSource !== ''" >
      <div class="banner" id="bannerImage"
          [ngStyle]="{'background-image': 'url(' + getImageSrc() + ')', 'height': height+'px' }">
      </div>
    </div>

    <div *ngIf="!imageSource || imageSource === ''">
      <div class="default" id="bannerDefault"
           [ngStyle]="{ 'background-color': backgroundColor, 'height': height+'px' }">
      </div>
    </div>
  `,
  styleUrls: ['./banner.css'],
})
export default class BannerComponent { 
  @Input() backgroundColor: string;
  @Input() height: number;
  @Input() imageSource: string;
  @Input() imageOptions: string;

  getImageSrc() {
    if(this.imageOptions) {
      return this.imageSource = this.imageOptions;
    }

    if(this.imageSource !== '') {
      return this.imageSource;
    }

  }
}

