  encapsulation: ViewEncapsulation.None
})
export default class ColorPickerComponent {

  isGradientPicker: boolean = false;
  color: string = '#A706D8';
  secondColor: string;

  @Output() close = new EventEmitter();
  @Output() done = new EventEmitter();

  handleColorChanged(event: CustomEvent) {
    this.color = event.detail.value;
  }

  handleSecondColorChanged(event: CustomEvent) {
    this.secondColor = event.detail.value;
  }

  onCancel() {
    this.close.emit(true);
  }

  onDone() {
    if(this.isGradientPicker) {
      return this.done.emit({
        color: this.color,
        secondColor: this.secondColor,
      });
    }

    return this.done.emit(this.color);
  }

}

