import { Component, Input, TemplateRef, ViewEncapsulation } from '@angular/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import 'vanilla-colorful';

@Component({
  selector: 'adstate-color-card',
  templateUrl: './colors.html',
  styleUrls: ['./colors.scss'],
  encapsulation: ViewEncapsulation.None
})
export default class ColorsCardComponent {

  @Input() color: string = '#9540bf';
  @Input() lightFontChecked: boolean = true;
  @Input() darkFontChecked: boolean = false;

  modalRef: BsModalRef;

  constructor(private modalService: BsModalService) { }

  handleColorChanged(event: CustomEvent) {
    this.color = event.detail.value;
  }

  openModal(template: TemplateRef<any>) {
    this.modalRef = this.modalService.show(template);
    this.modalRef.setClass('');
  }

  onDone(event) {
    if (typeof event === 'string') {
      this.color = event;
    } else {
      this.color = `linear-gradient(to right, ${event.color}, ${event.secondColor})`
    }

    const root = document.documentElement;
    return root.style.setProperty('--adstate-primary-gradient', this.color);
  }

  onLightFontChange(isChecked: boolean) {
    if (isChecked && this.darkFontChecked) {
      this.lightFontChecked = true;
      this.darkFontChecked = false;
    }
  }

  onDarkFontChange(isChecked: boolean) {
    if (isChecked && this.lightFontChecked) {
      this.lightFontChecked = false;
      this.darkFontChecked = true;
    }
  }


}

