import { Component, Input } from '@angular/core';

@Component({
  selector: 'storybook-icon',
  template: `    
    <div class="user-list-item-feature"
         [ngClass]="{'disabled': isDisabled}">
        <a class="icon-link" 
           disabled="isDisabled"
           href="{{ linkHref }}"
           target="_blank"
           [attr.aria-label]="label"
           [style.pointerEvents]="isDisabled ? 'none' : 'inherit'"
           >
          <img class="icon-image"
               [ngStyle]="getStyles()"
               [src]="icon" alt="icon">
          <span class="icon-label"> {{ label }} </span>
        </a>
    </div>
    `,
  styleUrls: ['./icon.scss'],
})
export default class IconComponent {
  @Input() icon: string;
  @Input() isDisabled: boolean;
  @Input() label: string;
  @Input() background: string = 'var(--adstate-primary-800)';
  @Input() height: number;
  @Input() width: number;
  @Input() unit: string = 'rem';
  @Input() linkHref: string = 'https://omp.adstate.net/memorial_page/memorial_page_personal_info.php?order_id=3767242&set_site_id=120&cat=home&sign=25cb3926af715015bf4a17eff22b8025';

  getStyles() {
    return {
      width: `${this.width}${this.unit}`,
      height: `${this.height}${this.unit}`,
      backgroundColor: this.isDisabled ? this.hexToRgba(this.background) : this.background
    }
  }

  hexToRgba(hexStringColor: string): string {

    const rgbaColor = hexStringColor.replace(/^#?([a-f\d])([a-f\d])([a-f\d])$/i
      , (m, r, g, b) => '#' + r + r + g + g + b + b)
      .substring(1).match(/.{2}/g)
      .map(x => parseInt(x, 16));


    return `rgba(${rgbaColor[0]}, ${rgbaColor[1]}, ${rgbaColor[2]}, 0.28)`

  }
}

