import { Component, Input } from '@angular/core';
import { DomSanitizer } from '@angular/platform-browser';

@Component({
  selector: 'adstate-logo-type',
  templateUrl: './logo-type.html',
  styleUrls: ['./logo-type.scss']
})
export default class LogoTypeComponent {
  
  @Input() imageSource: string = 'assets/images/logo.png';
  @Input() logoAlign: string = 'center';

  imgURL: any;

  constructor(private sanitizer: DomSanitizer) {}

  removeLogo() {
    this.imgURL = null;
  }

  allowDrop(ev) {
    ev.preventDefault();
  }

  drag(ev) {
    ev.dataTransfer.setData("text", ev.target.id);
  }

  drop(ev) {
    ev.preventDefault();
    const data = ev.dataTransfer.files;
    this.preview(data);
  }

  preview(files) {

    if (files.length === 0)
      return;

    let mimeType = files[0].type;
    if (mimeType.match(/image\/*/) == null) {
      const message = "Only images are supported.";
      return;
    }

    let reader = new FileReader();
    reader.readAsDataURL(files[0]); 
    reader.onload = (_event) => { 
      const dataUrl = reader.result;
      this.imgURL = this.sanitizer.bypassSecurityTrustResourceUrl(dataUrl as string) as string;
    }
  }

}

