        hasIcon: false
      },
      {
        value: 'Terms',
        hasIcon: false
      },
      {
        value: 'Privacy policy',
        hasIcon: false
      },
      {
        value: 'Cookie policy',
        hasIcon: false
      },
      {
        value: 'Administrator login',
        hasIcon: true
      }
    ],
    label: 'Menu',
    icon: 'lock',
    iconPlace: 'Left',
  }

  isFixedPosition: boolean = false;

  constructor() {
    window.onscroll = () => this.scrollFunction();
  }

  scrollFunction() {

    if (document.body.scrollTop > 70 || document.documentElement.scrollTop > 70) {
      document.getElementById('bannerImage').style.height = "200px";
      document.getElementById('bannerImage').style.filter = "drop-shadow(rgba(0, 0, 0, 0.25) 0px 4px 4px)";
      
      return setTimeout(() => {
        this.isFixedPosition = true;
      }, 300);
    }


    document.getElementById('bannerImage').style.height = "400px";
    document.getElementById('bannerImage').style.filter = 'none';
    
    return setTimeout(() => {
      this.isFixedPosition = false;
    }, 300);
  }

}

