import { from } from 'rxjs';
import { iconSource } from '../icon/icon';

@Component({
  selector: 'storybook-search-result-row',
  templateUrl: './row.html',
  styleUrls: ['./row.scss'],
})
export default class RowComponent implements OnInit {
  
  @Input() user: any;

  iconSource = iconSource;
  imageLoader = true;

  constructor(private renderer: Renderer2) { }

  ngOnInit(): void {
    this.getData();
  }

  getData(): void {
    if(this.user) {
      return;
    }

    from(import('../assets/data.json')).subscribe(data => {
      this.user = data.content[0];
    });

    return;
  }

  handleErrorImage(element): void {
    const imagePlaceHolder = '/default_profile_picture.jpg';
    this.renderer.setAttribute(element, 'src', imagePlaceHolder);
  }

  onLoadImage(): void {
    this.imageLoader = false;
  }
}

