@Component({
  selector: 'adstate-save-discard-change',
  templateUrl: './save-discard-change.component.html',
  styleUrls: ['./save-discard-change.component.scss']
})
export class SaveDiscardChangeComponent {

  @Output() discardChanges = new EventEmitter();
  @Output() saveSettings = new EventEmitter();

  @Input() color: string = '#c43eff';
  @Input() isSaving: boolean = false;
  @Input() isDiscarding: boolean = false;

  constructor() { }

  onDiscardChanges(): void {
    this.isDiscarding = true;
    setTimeout(() => { this.isDiscarding = false }, 2000);
    this.discardChanges.emit(true);
  }

  onSaveSettings(): void {
    this.isSaving = true;
    setTimeout(() => { this.isSaving = false }, 2000);
    this.saveSettings.emit(true);
  }
}

