import { Component, Input, Renderer2 } from '@angular/core';
import { from } from 'rxjs';
import { iconSource } from '../icon/icon';

@Component({
  selector: 'storybook-search-result',
  templateUrl: './search-result.html',
  styleUrls: ['./search-result.scss'],
})
export default class SearchResultComponent {
  @Input() icon: string;
  @Input() isDisabled: boolean;
  @Input() label: string;
  @Input() background: string;
  @Input() height: number;
  @Input() width: number;
  @Input() unit: string = 'rem';
  @Input() isOneRow: boolean = true;

  users = [];
  iconSource = iconSource;
  imageLoader = true;
  
  constructor(private renderer: Renderer2) {
    this.getData();
  }

  getData() {
    from(import('../assets/data.json')).subscribe(data => {
      this.users = data.content;
    })
  }

  getStyles() {
    return {
      width: `${this.width}${this.unit}`,
      height: `${this.height}${this.unit}`,
      backgroundColor: this.background,
    }
  }

  handleErrorImage(element): void {
    const imagePlaceHolder = '/default_profile_picture.jpg';
    this.renderer.setAttribute(element, 'src', imagePlaceHolder);
  }

  onLoadImage(): void {
    this.imageLoader = false;
  }
}

