  @Input() includeLanguageSelect: boolean = false;

  snippetLink = `<script src="${this.link}"></script>`;

  constructor() {
  }

  ngOnChanges(changes: SimpleChanges): void {
    if(changes) {
      this.snippetLink = `<script src="${this.link}"></script>`;
    }
  }

  onCopySnippet(): void {
    this.pop.show();
    setTimeout(() => this.pop.hide(), 1000);
  }

}

